INSERT INTO `about` VALUES ('1001', '1', '1001', '1', '0', '展会概况', '1', '', '&lt;p class=&quot;MsoListParagraph&quot; style=&quot;color:#696969;font-family:微软雅黑;text-indent:0cm;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;', '0', '0', '', '', '', null, '1', '1534140322', '1536575846', 'messe', '', '展会概况', '展会概况', '', '1'), ('1002', '2', '1001,1002', '1', '1001', '关于展会', '1', '', '&lt;table class=&quot;responsive&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;th width=&quot;25%&quot; bgcolor=&quot;#333333&quot;&gt;\r\n				&lt;span style=&quot;color:#FFFFFF;&quot;&gt;展会名称&lt;/span&gt; \r\n			&lt;/th&gt;\r\n			&lt;th width=&quot;75%&quot; bgcolor=&quot;#333333&quot;&gt;\r\n				&lt;span style=&quot;font-weight:bold;color:#FFFFFF;&quot;&gt; 北京世界食品博览会 &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#FFFFFF;&quot;&gt; 集萃全球美味,对话活力市场 &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#FFFFFF;&quot;&gt; 覆盖北亚地区食品行业贸易、渠道、 &lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#FFFFFF;&quot;&gt; 产业的年度盛宴 &lt;/span&gt; \r\n			&lt;/th&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;th colspan=&quot;2&quot; bgcolor=&quot;#333333&quot;&gt;\r\n				&lt;div class=&quot;theadBorder&quot;&gt;\r\n				&lt;/div&gt;\r\n			&lt;/th&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;th bgcolor=&quot;#333333&quot;&gt;\r\n				&lt;span style=&quot;color:#FFFFFF;&quot;&gt;举办时间&lt;/span&gt; \r\n			&lt;/th&gt;\r\n			&lt;th bgcolor=&quot;#333333&quot;&gt;\r\n				&lt;span style=&quot;color:#FFFFFF;&quot;&gt;2018年11月21-23日&lt;/span&gt; \r\n			&lt;/th&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;举办时间\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				科隆展览（中国）有限公司 &lt;br /&gt;\r\n中国食品土畜进出口商会\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;强力推动\r\n			&lt;/td&gt;\r\n			&lt;td&gt;\r\n				德国科隆世界食品博览会\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&amp;nbsp;