INSERT INTO `activity_child` VALUES ('100002', '10015', '赛事基本信息', '', '&lt;p&gt;\r\n	赛事名称 &amp;nbsp; &amp;nbsp;中餐烹饪世界锦标赛(团体赛)\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	赛事时间 &amp;nbsp; &amp;nbsp;2017年8月30日-9月1日\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	赛事地点 &amp;nbsp; &amp;nbsp;中国国际展览中心E3馆（新馆） 烹饪赛事专区\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;', 'http://', '255', '1534256750', '1534256750', '-1'), ('100003', '10015', '参赛报名', '', '&lt;p&gt;\r\n	报名注册\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	1.参赛团队须登陆赛事官方网站www.ccwc2015.com迚行登记注册；\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	2.每支参赛团队参赛费为美金$600/人民币¥4,200, 以VISA或MASTER信用卡于网上登记时一并支付；\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	3.网上登记日期: 2017年4月1日- 6月30日；\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	4.参赛团队完成网上登记注册代表接受所有参赛规则。\r\n&lt;/p&gt;\r\n&lt;br /&gt;', 'http://', '255', '1534258008', '1534258008', '-1'), ('100004', '10016', '赛事基本信息', '', '&lt;p&gt;\r\n	个人赛：前菜，刺身拼盘，寿司拼盘。共3品\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	团体赛（3人/组）：前菜8寸，刺身拼盘，煮物，寿司拼盘。共4品\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	个人比赛作品以一套店铺销售价500元为标准，团体比赛作品以店铺销售价1000元为标准。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	参赛个人和团体现场制作的料理作品。评委将对比赛完成作品进行现场品尝。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	参赛资格：从事日料行业的个人和企业（个人参赛者有5年以上的工作经验）。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	报名方式：向中国饭店协会日料委员会筹备组提出申请。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	联系人：邢程13910585635，金勇13311217711姜炳升13911692183\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	联系电话：010-68391597,683912456、传真：010-68391597、E-mail：942580819@QQ.com\r\n&lt;/p&gt;', 'http://', '255', '1534258754', '1534258922', '-1'), ('100005', '10027', '关于洲际酒店集团', '/201808/0818202353.jpeg', '&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-size:10px;&quot;&gt;英国洲际酒店集团&lt;/span&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;PLC[伦敦证券交易所：IHG，纽约证券交易所：IHG（ADRs）]在全球拥有十大闻名遐迩的酒店品牌，其中包括洲际酒店及度假村、Kimpton酒店及餐饮集团、华邑&amp;reg;酒店及度假村、皇冠假日酒店及度假村、英迪格酒店、EVEN酒店、假日酒店及度假村、智选假日酒店、Staybridge Suites&amp;reg;和Candlewood Suites&amp;reg;。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-size:10px;&quot;&gt;洲际酒店集团旗下拥有、管理、出租或托管的酒店多达&lt;/span&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;5,000多家，共有近750,000多间客房，遍布全球将近100多个国家和地区，并有超过1,400家在建酒店。洲际酒店集团拥有世界首个也是最大的酒店忠诚客户计划——IHG&amp;reg;优悦会，目前在全球拥有超过9,600万会员。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-size:10px;&quot;&gt;洲际酒店集团有限公司是集团控股公司，成立于英国，分别在英格兰及威尔士注册。洲际酒店集团在全球拥有超过&lt;/span&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;350,000名员工，分别在各地的酒店及集团办公室工作。&lt;/span&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;如需了解洲际酒店集团旗下所有酒店品牌的信息或进行网上预订，请访问&lt;/span&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;www.ihg.com。如需优悦会的信息请访问www.ihgrewardsclub.com。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot;&gt;\r\n	&lt;span style=&quot;font-size:10px;&quot;&gt;如需洲际酒店集团的最新消息，请访问www.ihg.com/media，www.twitter.com/ihgplc，www.facebook.com/ihg 或&lt;/span&gt;&lt;a href=&quot;http://www.youtube.com/ihgplc&quot;&gt;&lt;u&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;www.youtube.com/ihgplc&lt;/span&gt;&lt;/u&gt;&lt;/a&gt;&lt;span style=&quot;font-size:10px;&quot;&gt;。&lt;/span&gt;\r\n&lt;/p&gt;', 'http://', '255', '1534593260', '1534755649', '1');
