CREATE TABLE `article_category` (
  `article_category_id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `title_sub` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `desc_s` text CHARACTER SET utf8 COLLATE utf8_bin,
  `desc` text CHARACTER SET utf8 COLLATE utf8_bin,
  `template` int(1) DEFAULT NULL,
  `banner_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `banner_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `banner_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `page_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `page_keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `page_description` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `sort_id` int(11) DEFAULT NULL,
