DROP TABLE IF EXISTS `member`;
CREATE TABLE `member` (
  `member_id` int(11) NOT NULL AUTO_INCREMENT,
  `mobile` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '手机号',
  `email` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `username` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '会员账号',
  `gender` tinyint(3) NOT NULL DEFAULT '1' COMMENT '1 男 2 女',
  `avatar` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `password` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '会员密码',
  `wechat_openid` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `wechat_avatar` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '微信头像地址',
  `wechat_nickname` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '微信昵称',
  `wechat_language` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'en 英文 cn 中文',
  `city` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `province` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `country` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `device_reg` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `type_reg` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '注册类型',
  `ip_create` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `ip_reg` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '注册ip',
  `time_reg` int(11) DEFAULT NULL COMMENT '注册时间',
  `ip_bind` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `time_bind` int(11) DEFAULT NULL COMMENT '换绑时间',
  `time_create` int(11) DEFAULT NULL,
  `time_update` int(11) DEFAULT NULL,
  `status` tinyint(3) NOT NULL DEFAULT '1',
  PRIMARY KEY (`member_id`)
) ENGINE=MyISAM AUTO_INCREMENT=130 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

